sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	initialize: function(){

	},

	isDatePast: function(strDate) {
		var dt = new GlideDateTime(strDate);
		var today = new GlideDateTime();
		if (dt.before(today))
			return true;
		return false;
	},

	isDateToday: function(strDate) {
		var dt = new GlideDateTime(strDate);
		var today = new GlideDateTime();
		if (dt.getDate() == today.getDate())
			return true;
		return false;
	},

    type: 'NeedItUtils'
});
